mapper.select:
select id, goods_id, buyer_id, seller_id, last_message, last_message_at, unread_for_buyer, unread_for_seller, created_at
from conversation
where id = #{id}

select id, goods_id, buyer_id, seller_id, last_message, last_message_at, unread_for_buyer, unread_for_seller, created_at
from conversation
where goods_id = #{goodsId}
  and buyer_id = #{buyerId}
  and seller_id = #{sellerId}
limit 1

mapper.update:
update conversation
set last_message = #{lastMessage},
    last_message_at = now(),
    unread_for_buyer = unread_for_buyer + #{unreadForBuyerDelta},
    unread_for_seller = unread_for_seller + #{unreadForSellerDelta}
where id = #{conversationId}

update conversation
set unread_for_buyer = 0
where id = #{conversationId}

update conversation
set unread_for_seller = 0
where id = #{conversationId}
