mapper.insert:
INSERT INTO orders (
    order_no, user_id, seller_id, total_amount, status, payment_status,
    payment_method, payment_time, delivery_address, contact_name, contact_phone,
    remark, create_time, update_time, complete_time
) VALUES (
    #{orderNo}, #{userId}, #{sellerId}, #{totalAmount}, #{status}, #{paymentStatus},
    #{paymentMethod}, #{paymentTime}, #{deliveryAddress}, #{contactName}, #{contactPhone},
    #{remark}, #{createTime}, #{updateTime}, #{completeTime}
)

mapper.select:
SELECT * FROM orders
WHERE user_id = #{userId}

    AND status = #{status}

ORDER BY create_time DESC

SELECT * FROM orders
WHERE user_id = #{userId}
ORDER BY create_time DESC

SELECT * FROM orders
WHERE seller_id = #{sellerId}
ORDER BY create_time DESC

mapper.update:
UPDATE orders
status = #{status}, payment_status = #{paymentStatus}, payment_method = #{paymentMethod}, payment_time = #{paymentTime}, complete_time = #{completeTime}, update_time = #{updateTime},
WHERE id = #{id}
